import React, { useState } from "react";
// import { connect } from "react-redux";
import PropTypes from 'prop-types';
import { record } from "../actions/record";

const Player = props => {
    const [ formData, setFormData ] = useState({
        clicks: '',
        initials: ''
    });

    const { clicks, initials } = formData;

    const onChange = e => setFormData({
        ...formData, [e.target.name]: e.target.value
    });

    const onSubmit = e => {
        e.preventDefault();
        record(clicks, initials);
    };

    return (
        <div>
            <form className="form" onSubmit={e => onSubmit(e)}>
                <div className="form-group">
                    <input placeholder="Initials" name="initials" value={initials} onChange={e => onChange(e)} required/>
                </div>
                <input type="submit" className="btn btn-primary" value="Join the Hall of Fame"/>
            </form>
        </div>
    )
};

Player.propTypes = {
    clicks: PropTypes.number.isRequired,
    initials: PropTypes.string.isRequired
};

export default Player;
