import React, {Component} from "react";
// import { connect } from "react-redux";
import PropTypes from 'prop-types';
import {record} from "../actions/record";

class Player extends Component {
    constructor(props) {
        super(props);
        this.initials = "";
        this.clicks = props.clicks;
    }

    // onChange(e){
    //     this.initials = e.value;
    //     console.log("this is what e is right now" + e.innerText);
    // };

    onSubmit = e => {
        e.preventDefault();
        let initials = document.getElementById("inputInitials").innerText;
        console.log("initials on submit" + initials);
        record(this.clicks, initials);
    };

    render() {
        return (
            <div>
                <form id="playerForm" className="playerForm" onSubmit={e => this.onSubmit(e)}>
                    <div className="form-group">
                        <input id="inputInitials" placeholder="Initials" name="initials" required/>
                    </div>
                    <input type="submit" className="btn btn-primary" value="Join the Hall of Fame"/>
                </form>
            </div>
        )
    }
}

Player.propTypes = {
    clicks: PropTypes.number.isRequired,
    initials: PropTypes.string
};

export default Player;
