import React, {Component, useState} from "react";
// import { connect } from "react-redux";
import PropTypes from 'prop-types';
import {record} from "../actions/record";

class Player extends Component {
    constructor(props) {
        super(props);
        this.initials = "";
        this.clicks = props.clicks;
    }

    getInitialState(){
        return {clicks: 0};
    }

    updateClicks(clickCount) {
        this.setState({clicks: clickCount});
    }
    onChange(e){
        this.initials = e.value;
    };

    onSubmit = e => {
        e.preventDefault();
        // this.updateClicks(something.clicks);
        record(this.clicks, this.initials);
    };

    render() {
        return (
            <div>
                <form id="playerForm" className="playerForm" onSubmit={e => this.onSubmit(e)}>
                    <div className="form-group">
                        <input id="inputClicks" placeholder={this.clicks} name="clicks" />
                        <input id="inputInitials" placeholder="Initials" name="initials" onChange={e => this.onChange(e)}/>
                    </div>
                    <input type="submit" className="btn btn-primary" value="Join the Hall of Fame"/>
                </form>
            </div>
        )
    }
}

Player.propTypes = {
    clicks: PropTypes.number.isRequired,
    initials: PropTypes.string
};

export default Player;
